tests.forEach(function testInit (config) {
    test(function() {
        var d = new Date(config.date);

        // Without offset or timezone specified, the system local time will be assumed.
        // This cannot be tested.
        if (config.options && (!config.options.hasOwnProperty("offset") || config.options.offset === undefined)) {
            if (config.hasOwnProperty("timeZone")) {
                assert_own_property(window, "Intl", "This test requires support for the Intl API.");
                assert_own_property(window.Intl, "DateTimeFormat", "This test requires support for the Intl.DateTimeFormat API.");
                config.options.offset = d.lookupTimezoneOffset(config.timeZone);
            } else {
                assert_unreached("Local system timezone cannot be tested. The test is broken. Please specify options.offset or timeZone.");
            }
        }

        var actual = d.format(config.pattern, config.options);
        assert_equals(actual, config.expected, "The formatted date should be as expected.");
    }, config.desc);
});

